//Before using any symbol or library we will have
//to import it
import React, { Fragment } from "react";
import shortid from "shortid";
import Number from "./Number";
//import App from "../App";

const genRandomNumbers = (size) => {
    let result = [];
    for (let i = 0; i < size; i++) {
        result.push({
            id: shortid.generate(),
            value: Math.floor(Math.random() * 1000),
        });
    }
    return result;
}

class NumberList extends React.Component {
    constructor() {
        super();
        this.state = {
            list: genRandomNumbers(150)
        }
    }

    onAdd() {
        this.setState({
            list: this.state.list,
        })
    }

    render() {
        let { list } = this.state;
        return (
            <Fragment>
                <div className="numbers">
                    {list.map(obj => <Number key={obj.id} value={obj.value} />)}
                </div>
                <button onClick={this.onAdd}>Add</button>
            </Fragment>
        );
    }
}

// We have to export this component so that
// it is seen from outside this module
export default NumberList;
